import pymysqldef getConnection():    connection = pymysql.connect(host='127.0.0.1', port=3306, user='root', password='123456', db='test')    return connectiondef add():    sql = "insert into test(id, uname, grade) values (%s,%s,%s)"    connection = getConnection()    cursor = connection.cursor()    l = []    for i in range(1, 100000):        t = (0, "quan" + str(i), i)        l.append(t)    cursor.executemany(sql, l)    cursor.close()    connection.commit()    connection.close()def add_seperate():    sql = "insert into test(id, uname, grade) values (%s,%s,%s)"    connection = getConnection()    cursor = connection.cursor()    for i in range(1, 100000):        t = (0, "quan" + str(i), i)        cursor.execute(sql, t)        connection.commit()    cursor.close()    connection.close()if __name__ == "__main__":    add_seperate()